const API_BASE_URL_DEVELOPMENT = "http://localhost:5000";
const API_BASE_URL_PRODUCTION = "https://APPNAME.AZUREWEBSITES.NET";

const ENDPOINTS = {
    GET_ALL_PRODUCTS: "api/product",
    GET_PRODUCT_BY_ID:"api/product"
};

const development = {
    API_URL_GET_ALL_PRODUCT:`${API_BASE_URL_DEVELOPMENT}/${ENDPOINTS.GET_ALL_PRODUCTS}`,
    API_URL_GET_PRODUCT_BY_ID:`${API_BASE_URL_DEVELOPMENT}/${ENDPOINTS.GET_PRODUCT_BY_ID}`,

};
const production = {
    API_URL_GET_ALL_PRODUCT:`${API_BASE_URL_PRODUCTION}/${ENDPOINTS.GET_ALL_PRODUCTS}`,
    API_URL_GET_PRODUCT_BY_ID:`${API_BASE_URL_PRODUCTION}/${ENDPOINTS.GET_PRODUCT_BY_ID}`,
};

const Constants = process.env.NODE_ENV === 'development' ? development : production;

export default Constants;
