import { Checkbox, FormControl, FormControlLabel, FormGroup, Grid, Pagination, Paper, Radio, RadioGroup, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect } from "react";
import RadioButtonGroup from "../../App/Components/RadioButtonGroup";
import LoadingComponent from "../../App/Layout/LoadingComponent";
import { useAppDispatch, useAppSelector } from "../../App/Store/ConfigureStore";
import { fetchFilterAsync, fetchProductsAsync, productSelectors } from "./CatalogSlice";
import ProductList from "./ProductList";
import ProductSearch from "./ProductSearch";

const sortOptions = [
  { value: 'name', label: 'Alphabetical' },
  { value: 'priceDesc', label: 'Price - High to Low' },
  { value: 'Price', label: 'Price - Low to High' }
]

export default function Catalog() {

  const products = useAppSelector(productSelectors.selectAll);
  const { productsLoaded, status, filtersLoaded, gender, size, ProductParams } = useAppSelector(state => state.catalog)
  const dispatch = useAppDispatch();

  useEffect(() => {
    if (!productsLoaded) dispatch(fetchProductsAsync());
  }, [productsLoaded, dispatch]);

  useEffect(() => {
    if (!filtersLoaded) dispatch(fetchFilterAsync());
  }, [dispatch, filtersLoaded]);

  if (status.includes('pending')) return <LoadingComponent message="Loading Products" />
  return (
    <Grid container display='flex' justifyContent='space-between' spacing={4} sx={{ mt: 1 }}>
      <Grid item xs={3} >
        <Paper sx={{ mb: 2 }}>
          <ProductSearch></ProductSearch>
        </Paper>
        <Paper sx={{ mb: 2, p: 2 }}>
          <RadioButtonGroup 
            selectedValue={ProductParams.orderBy}
            options={sortOptions}
            onChange={eve}
          />
        </Paper>
        <Paper sx={{ mb: 2, p: 2 }}>
          <FormGroup>
            {gender.map(gender => (
              <FormControlLabel control={<Checkbox />} label={gender} key={gender} />
            ))}
          </FormGroup>
        </Paper>
        <Paper sx={{ mb: 2, p: 2 }}>
          <FormGroup>
            {size.map(size => (
              <FormControlLabel control={<Checkbox />} label={size} key={size} />
            ))}
          </FormGroup>
        </Paper>
      </Grid>
      <Grid item xs={9}>
        <ProductList products={products} />
      </Grid>
      <Grid item xs={3}/>
      <Grid item xs={9}>
        <Box display='flex'  justifyContent='space-between' alignItems='center'>
          <Typography>
            Displaying 1-6 of 20 Items
          </Typography>
          <Pagination count={6} color="primary" size="large"></Pagination>
        </Box>
      </Grid>
    </Grid>
  );
}
