import { Add, Delete, Remove } from "@mui/icons-material";
import { LoadingButton } from "@mui/lab";
import { Box, Button, Grid, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from "@mui/material";
import { useState } from "react";
import { Link } from "react-router-dom";
import Agent from "../../App/Api/Agent";
import { useAppDispatch, useAppSelector } from "../../App/Store/ConfigureStore";
import {removeBasketItemAsync, setBasket } from "./BasketSlice";
import BasketSummary from "./BasketSummary";


export default function BasketPage() {

  const { basket, status} = useAppSelector(state => state.basket);
  const dispatch = useAppDispatch();

  if (!basket) return <Typography variant="h3" textAlign="center" marginTop={3}>Your Basket is Empty</Typography>

  return (
    <>
    <TableContainer component={Paper} sx={{ marginTop: 3 }}>
      <Table sx={{ minWidth: 650 }}>
        <TableHead>
          <TableRow>
            <TableCell>Product</TableCell>
            <TableCell align="right">Price</TableCell>
            <TableCell align="center">Quantity</TableCell>
            <TableCell align="right">Subtotal</TableCell>
            <TableCell align="right"></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {basket.items.map((item) => (
            <TableRow
              key={item.productId}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}>
              <TableCell component="th" scope="row">
                <Box display='flex' alignItems='center'>
                  <img src={item.pictureUrl} alt={item.name} style={{ height: 50, marginRight: 20 }}></img>
                  <span>{item.name}</span>
                </Box>
              </TableCell>
              <TableCell align="right">${item.price.toFixed(2)}</TableCell>
              <TableCell align="center">
                <LoadingButton 
                  loading={status.includes('pendingRemoveItem'+item.productId)}
                  onClick={() => dispatch(removeBasketItemAsync({productId: item.productId}))} 
                  color="primary">
                  <Remove></Remove>
                </LoadingButton>
                {item.quantity}
                <LoadingButton 
                  loading={status.loading && status.name === 'add' + item.productId}
                  onClick={() => handleAddItem(item.productId, 'add' + item.productId)} color="primary">
                  <Add></Add>
                </LoadingButton>
              </TableCell>
              <TableCell align="right">${(item.quantity * item.price).toFixed(2)}</TableCell>
              <TableCell align="right">
                <LoadingButton 
                  loading={status.loading && status.name === 'del' + item.productId}
                  onClick={() => dispatch(removeBasketItemAsync({productId: item.productId, quantity: item.quantity}))} 
                  color="error">
                  <Delete></Delete>
                </LoadingButton>
              </TableCell>
            </TableRow>
          ))}          
        </TableBody>
      </Table>
    </TableContainer>
    <Grid container>
      <Grid item xs={6}/>
      <Grid item xs={6}>
            <BasketSummary></BasketSummary>
            <Button component={Link}
             to='/checkoutPage'
             variant="contained"
             size='large'
             fullWidth
             sx={{marginTop:2}}
             >Checkout</Button>
      </Grid>
    </Grid>
    </>
  )
};
